--

INSERT INTO `lxb_said` (`s_id`, `s_content`, `s_img`, `s_from`, `s_writer`, `s_view`, `create_ip`, `create_time`) VALUES
(1, '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Phoenix个人技术博客正式改版啦。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;与此同时由本人亲自主导的第一部面向PHP初学者的著作正式进入紧张的编写阶段，暂时就用这个博客来记录这部著作诞生的点点滴滴吧。</p>', 'userimg/2015-11-23/Phoenix.jpg', 'Win10', 'Phoenix', 1, '127.0.0.1', 1503273600),
(2, '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;距离开工之日已经整整过去一周时间啦，第一部分算是彻底结束了。在编书的同时还能顾得上博客的更新，我是不是也挺厉害的哈哈。允许我稍微自恋一下，哈哈哈，反正又没人看到我自恋的样子。坚持更新了一周，希望继续保持下去吧。<br/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;下周一开始更新PHP基础知识，打好底子才是最重要的。因此，我打算用4-6周时间结合具体项目实例重点讲解PHP基础部分，敬请期待吧。。。。<br/></p>', 'userimg/2015-11-23/Phoenix.jpg', 'Win 10', 'Phoenix', 1, '127.0.0.1', 1503844218);

